import React, { useState, useEffect } from 'react';
import './styles/App.css';  // Make sure your CSS file is included (optional)

function App() {
  // State to track if the app is loading
  const [isLoading, setIsLoading] = useState(true);

  // Simulate a loading delay (for example, API call or page load)
  useEffect(() => {
    // Set a timer to simulate data fetching or page loading
    setTimeout(() => {
      setIsLoading(false);  // Set loading state to false after the delay
    }, 2000);  // 2 seconds delay to simulate loading
  }, []);

  return (
    <div className="App">
      {isLoading ? (
        <div className="loading">
          <h2>Loading...</h2>
          {/* You can add a spinner component or CSS animation here */}
          <div className="spinner"></div>
        </div>
      ) : (
        // Show your main content after loading is complete
        <h1>Welcome to the UCF Parking App</h1>
      )}
    </div>
  );
}

export default App;
